function QueryFieldTypoComponent({ utilities, savedUserSettings, onSavedUserSettingsChange }) {
  const { useState, useEffect } = React;
  const [reportData, setReportData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function loadReport() {
      try {
        const result = await utilities.rq.RunQuery({
          QueryName: 'QuarterlyReport',
          CategoryPath: 'Reports/Financial',
          Parameters: {
            fiscalYear: 2024,
            department: 'Sales'
          }
        });

        if (result.Success) {
          // Common typos and case sensitivity issues
          const data = result.Results.map(row => ({
            // ❌ TYPO: "Revenu" instead of "Revenue" (missing 'e')
            revenue: row.Revenu,

            // ❌ TYPO: "Acccount" instead of "AccountName" (double 'c')
            account: row.Acccount,

            // ❌ TYPO: "Qarter" instead of "Quarter" (missing 'u')
            quarter: row.Qarter,

            // ❌ CASE ERROR: "accountname" instead of "AccountName"
            name: row.accountname,

            // ❌ TYPO: "Revanue" instead of "Revenue" (transposed letters)
            total: row.Revanue,

            // ❌ TYPO: "Departmant" instead of "Department" (wrong vowel)
            dept: row.Departmant,

            // ❌ TYPO: "Employeee" instead of "EmployeeCount" (extra 'e')
            employees: row.Employeee
          }));
          setReportData(data);
        }
      } catch (err) {
        console.error('Failed to load report:', err);
      } finally {
        setLoading(false);
      }
    }
    loadReport();
  }, []);

  if (loading) return <div>Loading...</div>;

  return (
    <div className="quarterly-report">
      <h2>Quarterly Report</h2>
      <ul>
        {reportData.map((item, idx) => (
          <li key={idx}>
            {/* Using typo'd field values */}
            {item.account} - Q{item.quarter}: ${item.revenue}
            <br />
            Dept: {item.dept} ({item.employees} employees)
          </li>
        ))}
      </ul>
    </div>
  );
}
