function ChartContainer({
  utilities,
  styles,
  components,
  callbacks,
  savedUserSettings,
  onSaveUserSettings,
  chartData = [],
  chartTitle = "Product Performance",
  yAxisLabel = "Count",
  showGrid = true
}) {
  const [chartInstance, setChartInstance] = useState(null);
  const [containerRef, setContainerRef] = useState(null);

  const initializeChart = useCallback(() => {
    if (!containerRef || !chartData.length) return;

    const option = {
      title: {
        text: chartTitle,
        left: 'center',
        textStyle: {
          color: '#333',
          fontSize: 16,
          fontWeight: 'bold'
        }
      },
      tooltip: {
        trigger: 'axis',
        axisPointer: {
          type: 'shadow'
        },
        formatter: (params) => {
          const data = params[0];
          return `${data.name}<br/>${yAxisLabel}: ${data.value}`;
        }
      },
      grid: {
        left: '3%',
        right: '4%',
        bottom: '3%',
        top: '15%',
        containLabel: true,
        show: showGrid,
        borderColor: '#e0e0e0'
      },
      xAxis: {
        type: 'category',
        data: chartData.map(item => item.ProductName),
        axisLabel: {
          rotate: 45,
          fontSize: 12,
          color: '#666'
        },
        axisTick: {
          alignWithLabel: true
        }
      },
      yAxis: {
        type: 'value',
        name: yAxisLabel,
        nameTextStyle: {
          color: '#666',
          padding: [0, 0, 0, 50]
        },
        axisLabel: {
          color: '#666'
        },
        splitLine: {
          show: showGrid,
          lineStyle: {
            color: '#e0e0e0',
            type: 'dashed'
          }
        }
      },
      series: [{
        name: chartTitle,
        type: 'bar',
        data: chartData.map(item => ({
          value: item.value,
          name: item.ProductName
        })),
        itemStyle: {
          color: new echarts.graphic.LinearGradient(0, 0, 0, 1, [
            { offset: 0, color: '#83bff6' },
            { offset: 0.5, color: '#188df0' },
            { offset: 1, color: '#188df0' }
          ])
        },
        emphasis: {
          itemStyle: {
            color: new echarts.graphic.LinearGradient(0, 0, 0, 1, [
              { offset: 0, color: '#2378f7' },
              { offset: 0.7, color: '#1264cc' },
              { offset: 1, color: '#1254a3' }
            ])
          }
        }
      }],
      dataZoom: [
        {
          type: 'inside',
          start: 0,
          end: 100
        },
        {
          type: 'slider',
          start: 0,
          end: 100
        }
      ],
      toolbox: {
        feature: {
          saveAsImage: {
            title: 'Save as Image',
            pixelRatio: 2
          },
          dataZoom: {
            title: {
              zoom: 'Area Zoom',
              back: 'Area Zoom Reset'
            }
          },
          restore: {
            title: 'Restore'
          }
        },
        right: 20,
        top: 10
      }
    };

    const chart = echarts.init(containerRef);
    chart.setOption(option);

    chart.on('click', (params) => {
      if (params.componentType === 'series' && params.data) {
        callbacks?.onChartClick?.(params.data.name);
      }
    });

    chart.on('mouseover', (params) => {
      if (params.componentType === 'series' && params.data) {
        callbacks?.onChartHover?.({
          productName: params.data.name,
          value: params.data.value
        });
      }
    });

    setChartInstance(chart);

    return () => {
      chart.dispose();
    };
  }, [containerRef, chartData, chartTitle, yAxisLabel, showGrid]);

  useEffect(() => {
    const cleanup = initializeChart();
    return cleanup;
  }, [initializeChart]);

  useEffect(() => {
    if (!chartInstance || !containerRef) return;

    const resizeObserver = new ResizeObserver(() => {
      chartInstance.resize();
    });

    resizeObserver.observe(containerRef);

    return () => {
      resizeObserver.disconnect();
    };
  }, [chartInstance, containerRef]);

  const updateChart = useCallback(() => {
    if (!chartInstance || !chartData.length) return;

    const option = {
      title: { text: chartTitle },
      xAxis: { data: chartData.map(item => item.ProductName) },
      series: [{
        data: chartData.map(item => ({
          value: item.value,
          name: item.ProductName
        }))
      }],
      yAxis: {
        name: yAxisLabel
      },
      grid: {
        show: showGrid
      }
    };

    chartInstance.setOption(option);
  }, [chartInstance, chartData, chartTitle, yAxisLabel, showGrid]);

  useEffect(() => {
    updateChart();
  }, [updateChart]);

  return (
    <div 
      ref={setContainerRef}
      style={{
        width: '100%',
        height: '100%',
        minHeight: '400px',
        backgroundColor: '#ffffff',
        borderRadius: '8px',
        boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
        padding: '20px'
      }}
    />
  );
}

function ToggleControl({
  utilities,
  styles,
  components,
  callbacks,
  savedUserSettings,
  onSaveUserSettings,
  initialMode = 'count',
  label = 'View By'
}) {
  const [selectedMode, setSelectedMode] = React.useState(
    savedUserSettings?.selectedMode || initialMode
  );

  const handleToggle = (event, newMode) => {
    if (newMode !== null) {
      setSelectedMode(newMode);
      onSaveUserSettings?.({
        ...savedUserSettings,
        selectedMode: newMode
      });
      callbacks?.onModeChange?.(newMode);
    }
  };

  const ToggleButtonGroup = MaterialUI.ToggleButtonGroup;
  const ToggleButton = MaterialUI.ToggleButton;
  const Typography = MaterialUI.Typography;
  const Box = MaterialUI.Box;

  return (
    <Box sx={{ marginBottom: 2 }}>
      {label && (
        <Typography variant="h6" sx={{ marginBottom: 1 }}>
          {label}
        </Typography>
      )}
      <ToggleButtonGroup
        value={selectedMode}
        exclusive
        onChange={handleToggle}
        aria-label="Toggle view mode"
      >
        <ToggleButton value="count" aria-label="Count mode">
          Count
        </ToggleButton>
        <ToggleButton value="revenue" aria-label="Revenue mode">
          Revenue
        </ToggleButton>
      </ToggleButtonGroup>
    </Box>
  );
}

