function DateRangeFilter({
  utilities,
  styles,
  components,
  callbacks,
  savedUserSettings,
  onSaveUserSettings,
  initialStartDate,
  initialEndDate,
  presets = [
    { label: 'Last 30 days', days: 30 },
    { label: 'Last 90 days', days: 90 },
    { label: 'Last 180 days', days: 180 },
    { label: 'This year', days: 365 }
  ]
}) {
  const [startDate, setStartDate] = useState(() => {
    if (initialStartDate) return initialStartDate;
    if (savedUserSettings?.startDate) return new Date(savedUserSettings.startDate);

    // Default to 30 days ago
    const date = new Date();
    date.setDate(date.getDate() - 30);
    return date;
  });

  const [endDate, setEndDate] = useState(() => {
    if (initialEndDate) return initialEndDate;
    if (savedUserSettings?.endDate) return new Date(savedUserSettings.endDate);
    return new Date();
  });

  const handleStartDateChange = (date) => {
    setStartDate(date);
    callbacks?.onDateRangeChange?.(date, endDate);
    onSaveUserSettings?.({
      ...savedUserSettings,
      startDate: date?.toISOString()
    });
  };

  const handleEndDateChange = (date) => {
    setEndDate(date);
    callbacks?.onDateRangeChange?.(startDate, date);
    onSaveUserSettings?.({
      ...savedUserSettings,
      endDate: date?.toISOString()
    });
  };

  const handlePresetClick = (days) => {
    const end = new Date();
    const start = new Date();
    start.setDate(start.getDate() - days);

    setStartDate(start);
    setEndDate(end);
    callbacks?.onDateRangeChange?.(start, end);
    onSaveUserSettings?.({
      ...savedUserSettings,
      startDate: start.toISOString(),
      endDate: end.toISOString()
    });
  };

  const { Box, Typography, Button, Paper, TextField } = MaterialUI;
  const { DesktopDatePicker } = MaterialUI;

  return (
    <Paper 
      elevation={1} 
      sx={{ 
        p: 2, 
        display: 'flex', 
        flexDirection: 'column', 
        gap: 2,
        ...styles?.paper
      }}
    >
      <Typography variant="h6" gutterBottom>
        Date Range Filter
      </Typography>

      <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap' }}>
        <DesktopDatePicker
          label="Start Date"
          value={startDate}
          onChange={handleStartDateChange}
          maxDate={endDate}
          renderInput={(params) => <TextField {...params} />}
        />

        <DesktopDatePicker
          label="End Date"
          value={endDate}
          onChange={handleEndDateChange}
          minDate={startDate}
          maxDate={new Date()}
          renderInput={(params) => <TextField {...params} />}
        />
      </Box>

      <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap' }}>
        {presets.map((preset) => (
          <Button
            key={preset.label}
            variant="outlined"
            size="small"
            onClick={() => handlePresetClick(preset.days)}
          >
            {preset.label}
          </Button>
        ))}
      </Box>
    </Paper>
  );
}

function LegendDisplay({ mode, position = 'bottom', utilities, styles, components, callbacks, savedUserSettings, onSaveUserSettings }) {
  const [items, setItems] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const loadLegendData = async () => {
    const result = await utilities.rq.RunQuery({
      QueryName: 'GetColorScheme',
      Parameters: { mode },
    });

    if (result.Success) {
      setItems(result.Results);
    }
    setIsLoading(false);
  };

  useState(() => {
    loadLegendData();
  }, []);

  const getLegendItems = useMemo(() => {
    if (isLoading) return [];

    return items.map(item => ({
      label: mode === 'count' ? `${item.metric} tickets` : `$${item.metric} revenue`,
      color: item.color,
      value: item.metric
    }));
  }, [items, mode, isLoading]);

  const getContainerStyle = () => {
    const base = {
      display: 'flex',
      flexDirection: 'row',
      gap: '16px',
      flexWrap: 'wrap',
      alignItems: 'center',
      background: styles.colors.background,
      border: `1px solid ${styles.colors.border}`,
      borderRadius: '4px',
      padding: '12px 16px',
      fontSize: styles.typography.fontSize.small,
    };

    switch (position) {
      case 'top':
        return {
          ...base,
          marginBottom: '16px',
        };
      case 'bottom':
        return {
          ...base,
          marginTop: '16px',
        };
      case 'left':
        return {
          ...base,
          flexDirection: 'column',
          alignItems: 'flex-start',
          marginRight: '16px',
          maxWidth: '200px',
        };
      case 'right':
        return {
          ...base,
          flexDirection: 'column',
          alignItems: 'flex-start',
          marginLeft: '16px',
          maxWidth: '200px',
        };
      default:
        return base;
    }
  };

  if (isLoading) {
    return <div style={getContainerStyle()}>Loading legend...</div>;
  }

  return (
    <div style={getContainerStyle()}>
      {getLegendItems.map((item, index) => (
        <div key={index} style={{
          display: 'flex',
          alignItems: 'center',
          gap: '8px',
          whiteSpace: 'nowrap'
        }}>
          <div style={{
            width: '12px',
            height: '12px',
            borderRadius: '2px',
            backgroundColor: item.color,
            flexShrink: 0
          }} />
          <span style={{
            color: styles.colors.text,
            fontSize: styles.typography.fontSize.small
          }}>
            {item.label}
          </span>
        </div>
      ))}
    </div>
  );
}

function ExportButton({ chartRef, data, filename = 'product-analysis', utilities, styles, components, callbacks, savedUserSettings, onSaveUserSettings }) {
  const [isMenuVisible, setIsMenuVisible] = useState(false);
  const [isExporting, setIsExporting] = useState(false);
  const [exportType, setExportType] = useState(null);

  const toggleMenu = () => {
    setIsMenuVisible(!isMenuVisible);
  };

  const handleExport = async (type) => {
    setIsExporting(true);
    setExportType(type);
    callbacks?.onExportStart?.();

    try {
      switch (type) {
        case 'png':
          await exportAsPNG();
          break;
        case 'svg':
          exportAsSVG();
          break;
        case 'csv':
          exportAsCSV();
          break;
      }
    } catch (error) {
      console.error('Export failed:', error);
    } finally {
      setIsExporting(false);
      setExportType(null);
      setIsMenuVisible(false);
      callbacks?.onExportComplete?.();
    }
  };

  const exportAsPNG = async () => {
    if (!chartRef?.current) return;

    const canvas = await html2canvas(chartRef.current);
    const url = canvas.toDataURL('image/png');
    downloadFile(url, `${filename}.png`, 'image/png');
  };

  const exportAsSVG = () => {
    if (!chartRef?.current) return;

    const chart = chartRef.current.getEchartsInstance();
    const svg = chart.renderToSVGString();
    const blob = new Blob([svg], { type: 'image/svg+xml' });
    const url = URL.createObjectURL(blob);
    downloadFile(url, `${filename}.svg`, 'image/svg+xml');
  };

  const exportAsCSV = () => {
    if (!data || data.length === 0) return;

    const headers = Object.keys(data[0]);
    const csvContent = [
      headers.join(','),
      ...data.map(row => 
        headers.map(header => {
          const value = row[header];
          return typeof value === 'string' && value.includes(',') ? `"${value}"` : value;
        }).join(',')
      )
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    downloadFile(url, `${filename}.csv`, 'text/csv');
  };

  const downloadFile = (url, fileName, mimeType) => {
    const link = document.createElement('a');
    link.href = url;
    link.download = fileName;
    link.style.display = 'none';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);

    if (url.startsWith('blob:')) {
      URL.revokeObjectURL(url);
    }
  };

  const menuItems = [
    { key: 'png', label: 'Export as PNG' },
    { key: 'svg', label: 'Export as SVG' },
    { key: 'csv', label: 'Export as CSV' }
  ];

  return (
    <div style={styles.exportButtonWrapper || { position: 'relative', display: 'inline-block' }}>
      <button
        onClick={toggleMenu}
        disabled={isExporting}
        style={styles.exportButton || {
          padding: '8px 16px',
          backgroundColor: '#1677ff',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer',
          display: 'flex',
          alignItems: 'center',
          gap: '4px'
        }}
      >
        {isExporting ? 'Exporting...' : 'Export'}
        <span style={{ marginLeft: '4px' }}>â–¼</span>
      </button>

      {isMenuVisible && (
        <div style={styles.exportMenu || {
          position: 'absolute',
          top: '100%',
          right: 0,
          backgroundColor: 'white',
          border: '1px solid #d9d9d9',
          borderRadius: '4px',
          boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
          zIndex: 1000,
          minWidth: '150px',
          marginTop: '4px'
        }}>
          {menuItems.map(item => (
            <button
              key={item.key}
              onClick={() => handleExport(item.key)}
              disabled={isExporting && exportType === item.key}
              style={styles.exportMenuItem || {
                display: 'block',
                width: '100%',
                padding: '8px 16px',
                textAlign: 'left',
                backgroundColor: 'transparent',
                border: 'none',
                cursor: 'pointer',
                fontSize: '14px',
                color: isExporting && exportType === item.key ? '#999' : '#333'
              }}
              onMouseEnter={(e) => {
                e.target.style.backgroundColor = '#f5f5f5';
              }}
              onMouseLeave={(e) => {
                e.target.style.backgroundColor = 'transparent';
              }}
            >
              {item.label}
            </button>
          ))}
        </div>
      )}
    </div>
  );
}

