function ProductInvoiceVolumeChart({utilities, styles, components, callbacks, savedUserSettings, onSaveUserSettings}) {
  const {ChartContainer, ToggleControl, DateRangeFilter, LegendDisplay, ExportButton} = components;
  const theme = MaterialUI.useTheme();

  // State management
  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState(null);
  const [chartData, setChartData] = React.useState([]);
  const [viewMode, setViewMode] = React.useState(savedUserSettings?.viewMode || 'count');
  const [dateRange, setDateRange] = React.useState({
    start: savedUserSettings?.startDate || dayjs().subtract(30, 'days').toDate(),
    end: savedUserSettings?.endDate || dayjs().toDate()
  });

  const chartRef = React.useRef(null);

  // Load data
  React.useEffect(() => {
    const loadData = async () => {
      try {
        setLoading(true);
        setError(null);

        const result = await utilities.rq.RunQuery({
          QueryName: "ProductInvoiceVolumeTrend"
        });

        if (result.Success) {
          setChartData(result.Results || []);
        } else {
          setError('Failed to load data');
        }
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, []);

  // Process chart data based on view mode
  const processedChartData = React.useMemo(() => {
    if (!chartData || chartData.length === 0) return [];

    return chartData.map(item => ({
      ProductName: item.ProductName,
      value: viewMode === 'count' ? item.InvoiceCount : item.TotalRevenue
    }));
  }, [chartData, viewMode]);

  // Handle view mode change
  const handleViewModeChange = (newMode) => {
    setViewMode(newMode);
    callbacks?.onViewModeChange?.(newMode);
    onSaveUserSettings?.({
      ...savedUserSettings,
      viewMode: newMode
    });
  };

  // Handle date range change
  const handleDateRangeChange = (startDate, endDate) => {
    const newRange = { start: startDate, end: endDate };
    setDateRange(newRange);
    callbacks?.onDateRangeChange?.(startDate, endDate);
    onSaveUserSettings?.({
      ...savedUserSettings,
      startDate: startDate,
      endDate: endDate
    });
  };

  // Handle product selection
  const handleProductSelect = (productName) => {
    callbacks?.onProductSelect?.(productName);
  };

  const yAxisLabel = viewMode === 'count' ? 'Invoice Count' : 'Revenue ($)';
  const chartTitle = viewMode === 'count' 
    ? 'Product Invoice Volume (Count)'
    : 'Product Invoice Volume (Revenue)';

  return (
    <MaterialUI.Box>
      <MaterialUI.Typography variant="h5" gutterBottom>
        Product Invoice Volume Analysis
      </MaterialUI.Typography>

      <MaterialUI.Box>
        <MaterialUI.Grid container spacing={3} alignItems="center">
          <MaterialUI.Grid item xs={12} sm={4}>
            <ToggleControl
              utilities={utilities}
              styles={styles}
              components={components}
              callbacks={callbacks}
              savedUserSettings={savedUserSettings}
              onSaveUserSettings={onSaveUserSettings}
              initialMode={viewMode}
              onModeChange={handleViewModeChange}
            />
          </MaterialUI.Grid>
          <MaterialUI.Grid item xs={12} sm={5}>
            <DateRangeFilter
              utilities={utilities}
              styles={styles}
              components={components}
              callbacks={callbacks}
              savedUserSettings={savedUserSettings}
              onSaveUserSettings={onSaveUserSettings}
              initialStartDate={dateRange.start}
              initialEndDate={dateRange.end}
              onDateRangeChange={handleDateRangeChange}
            />
          </MaterialUI.Grid>
          <MaterialUI.Grid item xs={12} sm={3}>
            <LegendDisplay
              utilities={utilities}
              styles={styles}
              components={components}
              callbacks={callbacks}
              savedUserSettings={savedUserSettings}
              onSaveUserSettings={onSaveUserSettings}
              mode={viewMode}
              position="right"
            />
          </MaterialUI.Grid>
        </MaterialUI.Grid>
      </MaterialUI.Box>

      <MaterialUI.Box>
        {loading && (
          <MaterialUI.Box display="flex" justifyContent="center" p={3}>
            <MaterialUI.CircularProgress />
          </MaterialUI.Box>
        )}

        {error && (
          <MaterialUI.Alert severity="error">
            {error}
          </MaterialUI.Alert>
        )}

        {!loading && !error && processedChartData.length > 0 && (
          <ChartContainer
            utilities={utilities}
            styles={styles}
            components={components}
            callbacks={callbacks}
            savedUserSettings={savedUserSettings}
            onSaveUserSettings={onSaveUserSettings}
            chartData={processedChartData}
            chartTitle={chartTitle}
            yAxisLabel={yAxisLabel}
            onChartClick={handleProductSelect}
          />
        )}

        {!loading && !error && processedChartData.length === 0 && (
          <MaterialUI.Box display="flex" justifyContent="center" p={3}>
            <MaterialUI.Typography variant="h6" color="textSecondary">
              No data available for the selected period
            </MaterialUI.Typography>
          </MaterialUI.Box>
        )}
      </MaterialUI.Box>

      {!loading && !error && (savedUserSettings?.showExport !== false) && (
        <MaterialUI.Box mt={2}>
          <ExportButton
            utilities={utilities}
            styles={styles}
            components={components}
            callbacks={callbacks}
            savedUserSettings={savedUserSettings}
            onSaveUserSettings={onSaveUserSettings}
            chartRef={chartRef}
            data={processedChartData}
            filename={`product-invoice-volume-${viewMode}`}
          />
        </MaterialUI.Box>
      )}
    </MaterialUI.Box>
  );
}
