function OrdersByDateRange({ utilities }) {
  const [orders, setOrders] = React.useState([]);

  React.useEffect(() => {
    async function fetchOrders() {
      const result = await utilities.rq.RunQuery({
        QueryName: 'Orders By Date',
        CategoryPath: 'Reports/Orders',
        Parameters: {
          FromDate: '01/15/2024',
          ToDate: '2024-13-45'
        }
      });
      if (result?.Success) {
        setOrders(result.Results || []);
      }
    }
    fetchOrders();
  }, []);

  return (
    <div>
      <h1>Orders Report</h1>
      <p>Found {orders.length} orders</p>
    </div>
  );
}
