function SimpleChartMissingValueField({ components, utilities }) {
  const { SimpleChart } = components;
  const [sales, setSales] = React.useState(null);

  React.useEffect(() => {
    async function loadData() {
      const results = await utilities.rv.RunView({
        EntityName: 'Sales',
        ExtraFilter: '',
        MaxRows: 1000
      });
      setSales(results.Results);
    }
    loadData();
  }, []);

  if (!sales) return <div>Loading...</div>;

  return (
    <div>
      <h1>Revenue by Product</h1>
      <SimpleChart
        entityName="Sales"
        data={sales}
        groupBy="ProductName"
        aggregateMethod="sum"
        chartType="bar"
        utilities={utilities}
      />
    </div>
  );
}
