function SimpleChartMinWithValuefield({ components, utilities }) {
  const { SimpleChart } = components;
  const [products, setProducts] = React.useState(null);

  React.useEffect(() => {
    async function loadData() {
      const results = await utilities.rv.RunView({
        EntityName: 'Products',
        ExtraFilter: '',
        MaxRows: 50
      });
      setProducts(results.Results);
    }
    loadData();
  }, []);

  if (!products) return <div>Loading...</div>;

  return (
    <SimpleChart
      entityName="Products"
      data={products}
      categoryField="Category"
      valueField="Price"
      aggregateMethod="min"
      chartType="column"
      title="Minimum Price by Category"
      utilities={utilities}
    />
  );
}
