function DataRefreshComponent({ utilities, callbacks, savedUserSettings, onSaveUserSettings }) {
  const [refreshCount, setRefreshCount] = useState(0);
  const [data, setData] = useState([]);

  // âœ… GOOD: callbacks is omitted - only runs once on mount
  useEffect(() => {
    const loadData = async () => {
      const result = await utilities.rv.RunView({
        EntityName: 'Members',
        MaxRows: 100
      });

      if (result.Success) {
        setData(result.Results || []);
        setRefreshCount(prev => prev + 1);

        // Notify parent of data load
        if (callbacks?.onDataLoaded) {
          callbacks.onDataLoaded(result.Results.length);
        }
      }
    };

    loadData();
  }, []);

  return (
    <div>
      <h3>Data Loaded {refreshCount} times</h3>
      <p>Records: {data.length}</p>
    </div>
  );
}
