function TestComponent({ utilities, savedUserSettings, onSavedUserSettingsChange }) {
  const [data, setData] = useState([]);

  useEffect(() => {
    utilities.rv.RunView({ EntityName: 'Users' })
      .then(result => {
        if (result.Success) {
          return result.Results || [];
        }
        return [];
      })
      .then(items => {
        setData(items);
      })
      .catch(error => {
        console.error('Error:', error);
      });
  }, []);

  return (
    <div>
      <h1>Test Component</h1>
      <p>Data count: {data.length}</p>
    </div>
  );
}
