function EntityFieldOptionalChainingComponent({ utilities, savedUserSettings, onSavedUserSettingsChange }) {
  const { useState, useEffect } = React;
  const [user, setUser] = useState(null);
  const [account, setAccount] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function loadData() {
      const userResult = await utilities.rv.RunView({
        EntityName: 'Users',
        MaxRows: 1
      });

      if (userResult.Success && userResult.Results.length > 0) {
        setUser(userResult.Results[0]);
      }

      const accountResult = await utilities.rv.RunView({
        EntityName: 'Accounts',
        MaxRows: 1
      });

      if (accountResult.Success && accountResult.Results.length > 0) {
        setAccount(accountResult.Results[0]);
      }

      setLoading(false);
    }
    loadData();
  }, []);

  if (loading) return <div>Loading...</div>;

  return (
    <div>
      <h1>User Profile</h1>

      {/* VIOLATION: InvalidField doesn't exist on Users entity */}
      <p>Invalid Field: {user?.InvalidField}</p>

      {/* VIOLATION: FakeProperty doesn't exist on Users entity */}
      <p>Fake: {user?.FakeProperty || 'N/A'}</p>

      {/* VIOLATION: NonExistent doesn't exist on Users entity */}
      <p>Non-existent: {user?.NonExistent?.toString()}</p>

      {/* VIOLATION: BadField doesn't exist on Accounts entity */}
      <p>Account Bad: {account?.BadField}</p>

      {/* VIOLATION: WrongProperty doesn't exist on Accounts entity */}
      <p>Wrong: {account?.WrongProperty ?? 'Default'}</p>

      {/* VIOLATION: Chained invalid access */}
      <p>Chained: {user?.InvalidField?.NestedBad}</p>

      {/* These would be CORRECT if the fields existed */}
      <p>Name: {user?.Name}</p>
      <p>Email: {user?.Email}</p>
      <p>Account Name: {account?.Name}</p>
    </div>
  );
}
