function TestComponent({ utilities, savedUserSettings, onSavedUserSettingsChange, initialData, filterValue }) {
  // Anti-pattern: copying props to state
  const [data, setData] = useState(initialData);
  const [filter, setFilter] = useState(filterValue);
  const [processedData, setProcessedData] = useState([]);

  // Anti-pattern: syncing props to state with useEffect
  useEffect(() => {
    setData(initialData);
  }, [initialData]);

  // Anti-pattern: syncing filter prop to state
  useEffect(() => {
    setFilter(filterValue);
  }, [filterValue]);

  // This creates unnecessary re-renders
  useEffect(() => {
    if (data && filter) {
      const filtered = data.filter(item => 
        item.Name.includes(filter)
      );
      setProcessedData(filtered);
    }
  }, [data, filter]);

  return (
    <div>
      <h1>Data View</h1>
      <input 
        value={filter} 
        onChange={e => setFilter(e.target.value)} 
      />
      {processedData.map(item => (
        <div key={item.ID}>{item.Name}</div>
      ))}
    </div>
  );
}
