function TestComponent({ utilities, savedUserSettings, onSavedUserSettingsChange }) {
  const [data, setData] = useState([]);
  const chartRef = useRef(null);

  useEffect(() => {
    // Initialize chart
    const chart = echarts.init(chartRef.current);

    async function loadData() {
      const result = await utilities.rv.RunView({
        EntityName: 'Revenue',
        MaxRows: 100
      });
      if (result.Success) {
        setData(result.Results || []);
        chart.setOption({
          xAxis: { type: 'category', data: result.Results.map(r => r.Month) },
          yAxis: { type: 'value' },
          series: [{ data: result.Results.map(r => r.Amount), type: 'line' }]
        });
      }
    }

    loadData();

    // Missing cleanup function - should call chart.dispose()
    // return () => { chart.dispose(); };
  }, []);

  return (
    <div>
      <h1>Revenue Chart</h1>
      <div ref={chartRef} style={{ width: '100%', height: 400 }} />
    </div>
  );
}
