function TestComponent({ utilities, savedUserSettings, onSavedUserSettingsChange }) {
  const [data, setData] = useState([]);

  useEffect(() => {
    async function loadData() {
      const result = await utilities.rq.RunQuery({
        QueryName: 'CustomerReport',
        CategoryPath: 'Reports/Customers',
        Parameters: {
          startDate: '2024-01-01',
          endDate: '2024-12-31',
          unknownParam: 'value'  // This parameter is not declared in query spec
        }
      });
      if (result.Success) {
        setData(result.Results || []);
      }
    }
    loadData();
  }, []);

  return (
    <div>
      <h1>Customer Report</h1>
      <p>Records: {data.length}</p>
    </div>
  );
}
