        CategoryPath: 'Sales/Reports'
      });

      if (result?.Success) {
        // Stage 2: Transform and aggregate
        const byMonth = result.Results.reduce((acc, sale) => {
          const month = sale.SaleDate?.substring(0, 7);
          if (!acc[month]) {
            acc[month] = {Month: month, Count: 0}; // ❌ Missing Revenue field
          }
          acc[month].Count++;
          return acc;
        }, {});

        // Stage 3: Convert to array for chart
        const chartArray = Object.values(byMonth);
        setChartData(chartArray);
      }
    }
    loadData();
  }, []);

  return (
    <div>
      <h3>Monthly Sales</h3>
      {/* ❌ Chart expects Revenue but transformation only kept Count */}
