function SpreadMultipleSourcesFixed({ utilities, components }) {
  const [combinedData, setCombinedData] = useState([]);
  const { DataGrid } = components;

  useEffect(() => {
    async function loadData() {
      const [members, orders] = await Promise.all([
        utilities.rv.RunView({ EntityName: 'Members' }),
        utilities.rv.RunView({ EntityName: 'Orders' })
      ]);

      if (members?.Success && orders?.Success) {
        // âœ… Explicit field naming to avoid conflicts
        const combined = members.Results.map(m => {
          const memberOrders = orders.Results.filter(o => o.MemberID === m.ID);
          const firstOrder = memberOrders[0];
          return {
            MemberID: m.ID,
            FirstName: m.FirstName,
            LastName: m.LastName,
            Email: m.Email,
            OrderID: firstOrder?.ID,
            OrderNumber: firstOrder?.OrderNumber,
            OrderAmount: firstOrder?.TotalAmount
          };
        });
        setCombinedData(combined);
      }
    }
    loadData();
  }, []);

  return (
    <div>
      <h3>Member Orders</h3>
      {/* Grid uses explicit field names */}
      <DataGrid 
        data={combinedData} 
        columns={[
          {field: 'FirstName', title: 'Name'},
          {field: 'MemberID', title: 'Member ID'},
          {field: 'OrderNumber', title: 'Order'}
        ]} 
      />
    </div>
  );
}
