function DataGridValidColumns({ components, utilities }) {
  const { DataGrid } = components;
  const [invoices, setInvoices] = React.useState(null);

  React.useEffect(() => {
    async function loadData() {
      const results = await utilities.rv.RunView({
        EntityName: 'Invoices',
        ExtraFilter: '',
        MaxRows: 100
      });
      setInvoices(results.Results);
    }
    loadData();
  }, []);

  if (!invoices) return <div>Loading...</div>;

  return (
    <div>
      <h1>Invoices List</h1>
      <DataGrid
        entityName="Invoices"
        data={invoices}
        columns={['InvoiceNumber', 'Member', 'Total', 'Status']}
        utilities={utilities}
      />
    </div>
  );
}
