function EntityArrayFilterValidFixed({ utilities }) {
  const [premiumMembers, setPremiumMembers] = useState([]);

  useEffect(() => {
    async function loadData() {
      const result = await utilities.rv.RunView({ EntityName: 'Members' });
      if (result?.Success) {
        // âœ… Filtering by existing field: MembershipType
        const filtered = result.Results.filter(m => m.MembershipType === 'Premium');
        setPremiumMembers(filtered);
      }
    }
    loadData();
  }, []);

  return (
    <div>
      <h3>Premium Members</h3>
      {premiumMembers.map((m, i) => (
        <div key={i}>{m.FirstName} {m.LastName}</div>
      ))}
    </div>
  );
}
