function QueryResultReduceInvalidFieldFixed({ utilities }) {
  const [categoryCount, setCategoryCount] = useState({});

  useEffect(() => {
    async function loadData() {
      const result = await utilities.rq.RunQuery({
        QueryName: 'Product Sales',
        CategoryPath: 'Sales/Reports'
      });
      if (result?.Success) {
        // âœ… Grouping by Category field that exists
        const counts = result.Results.reduce((acc, item) => {
          const cat = item.Category || 'Unknown';
          acc[cat] = (acc[cat] || 0) + 1;
          return acc;
        }, {});
        setCategoryCount(counts);
      }
    }
    loadData();
  }, []);

  return (
    <div>
      <h3>Products by Category</h3>
      {Object.entries(categoryCount).map(([cat, count]) => (
        <div key={cat}>{cat}: {count}</div>
      ))}
    </div>
  );
}
