function DataLoaderComponent({ utilities, savedUserSettings, onSavedUserSettingsChange }) {
  const { useState, useEffect } = React;
  const [viewData, setViewData] = useState([]);
  const [queryData, setQueryData] = useState([]);
  const [stats, setStats] = useState({ viewCount: 0, queryCount: 0 });
  const [errors, setErrors] = useState([]);

  useEffect(() => {
    async function loadAllData() {
      const errorList = [];

      // Load data from RunView
      const viewResult = await utilities.rv.RunView({
        EntityName: 'Products',
        MaxRows: 100,
        OrderBy: 'Name ASC'
      });

      // CORRECT: Check Success property before accessing data
      if (viewResult.Success) {
        // CORRECT: Access Results property for the data array
        setViewData(viewResult.Results || []);
        // CORRECT: Access RowCount property for total count
        setStats(prev => ({ ...prev, viewCount: viewResult.RowCount || 0 }));
      } else {
        // CORRECT: Access ErrorMessage property for error info
        errorList.push(viewResult.ErrorMessage || 'Failed to load products');
      }

      // Load data from RunQuery
      const queryResult = await utilities.rq.RunQuery({
        QueryName: 'CategorySummary',
        CategoryPath: 'Reports/Products',
        Parameters: {
          activeOnly: true
        }
      });

      // CORRECT: Same pattern for RunQuery results
      if (queryResult.Success) {
        setQueryData(queryResult.Results || []);
        setStats(prev => ({ ...prev, queryCount: queryResult.RowCount || 0 }));
      } else {
        errorList.push(queryResult.ErrorMessage || 'Failed to load category summary');
      }

      setErrors(errorList);
    }

    loadAllData();
  }, []);

  return (
    <div className="data-loader">
      <h1>Data Loader</h1>

      {errors.length > 0 && (
        <div className="errors">
          {errors.map((error, index) => (
            <p key={index} className="error">{error}</p>
          ))}
        </div>
      )}

      <div className="stats">
        <p>Products loaded: {stats.viewCount}</p>
        <p>Categories loaded: {stats.queryCount}</p>
      </div>

      <div className="products">
        <h2>Products</h2>
        <ul>
          {viewData.map(product => (
            <li key={product.ID}>{product.Name} - {product.Category}</li>
          ))}
        </ul>
      </div>

      <div className="categories">
        <h2>Category Summary</h2>
        <ul>
          {queryData.map((category, index) => (
            <li key={index}>{category.CategoryName}: {category.ProductCount} products</li>
          ))}
        </ul>
      </div>
    </div>
  );
}
